// Make sure your App.js has proper rendering code
import React from 'react';
import './App.css';
import ChatInterface from './components/ChatInterface';
import Dashboard from './components/Dashboard';
import Login from './components/Login';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

function App() {
  return (
    <Router>
      <div className="app">
        <Routes>
          <Route path="/login" element={<Login onLogin={() => true} />} />
          <Route path="/dashboard" element={<Dashboard onLogout={() => {}} />} />
          <Route path="/chat" element={<ChatInterface onLogout={() => {}} userRole="employee" />} />
          <Route path="/" element={<Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
